package CoreJava;

public class DisplayChar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       //  Java Program to Display Characters from A to Z using loop
		 char c;
		 
		 for(c = 'A'; c<= 'Z'; ++c)
		 {
			 System.out.println(" " + c);
		 }
	}

}
