package CoreJava;

public class whileexa {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        System.out.println("This is using while loop");
		int i=20;
		while(i>=1)
		{
		System.out.println(i);
		i--;
		}
        System.out.println("This is using do-while loop");

		int a=20;
		do {
			System.out.println(a);
		a--;
		}
		while(a>=2);
		{
			System.out.println(a);
            
		}
		
        System.out.println("This is using for loop");

		for(int b=20; b>=1; b--)
		{
			System.out.println(b);

		}
		}

}
